let player,squares=document.querySelectorAll(".square"),id=document.getElementById(this),moves=0,player1={name:"Player One",marker:"X",color:"red"},player2={name:"Player Two",marker:"0",color:"yellow"};function startGame(){squares.forEach(e=>e.innerHTML=""),moves=0}function changeUser(e){player=e%2==0?player1:player2,event.target.innerHTML=player.marker,event.target.style.color=player.color,event.target.style.pointerEvents="none",checkWinner(player.marker)}function checkWinner(e){return(checkRow(0,1,2,e)||checkRow(3,4,5,e)||checkRow(6,7,8,e)||checkRow(0,3,6,e)||checkRow(1,4,7,e)||checkRow(2,5,8,e)||checkRow(0,4,8,e)||checkRow(2,4,6,e))&&console.log(`${player.name} won`),!1}function checkRow(e,r,n,o){var c=!1;return getBox(r)==o&&getBox(r)==o&&getBox(n)==o&&(c=!0),c}function getBox(e){return document.getElementById(e).innerText}for(var i=0;i<squares.length;i++)squares[i].addEventListener("click",function(e){changeUser(moves),moves++});